mapper.update:
update EXPORT_C
INPUT_DATE=#{inputDate}, CONTRACT_IDS=#{contractIds}, CUSTOMER_CONTRACT=#{customerContract}, LCNO=#{lcno}, CONSIGNEE=#{consignee}, MARKS=#{marks}, REMARK=#{remark}, SHIPMENT_PORT=#{shipmentPort}, DESTINATION_PORT=#{destinationPort}, TRANSPORT_MODE=#{transportMode}, PRICE_CONDITION=#{priceCondition}, STATE=#{state}, GROSS_WEIGHT=#{grossWeight}, NET_WEIGHT=#{netWeight}, MEASUREMENT=#{measurement},
where EXPORT_ID=#{id}

mapper.delete:
delete from export_c
where export_id in

	#{id}

mapper.update:
update export_c
set state=#{state}
where export_id in

	#{id}
