mapper.select:
select * from ext_eproduct_c
where 1=1
 and EXPORT_PRODUCT_ID=#{exportProductId}

mapper.update:
update ext_eproduct_c
EXPORT_PRODUCT_ID=#{exportProductId}, FACTORY_ID=#{factoryId}, FACTORY_NAME=#{factoryName}, CTYPE=#{ctype}, PRODUCT_NO=#{productNo}, PRODUCT_IMAGE=#{productImage}, PRODUCT_DESC=#{productDesc}, CNUMBER=#{cnumber}, PACKING_UNIT=#{packingUnit}, PRICE=#{price}, AMOUNT=#{amount}, PRODUCT_REQUEST=#{productRequest}, ORDER_NO=#{orderNo},
where EXT_EPRODUCT_ID=#{id}

mapper.delete:
delete from ext_eproduct_c
where EXT_EPRODUCT_ID in

	#{id}
