mapper.select:
select USER_ID, USERNAME, PASSWORD, EMAIL, DEPARTMENT, CREATE_TIME, BIRTHDAY
from USER_LOGIN_C
where EMAIL=#{email,jdbcType=VARCHAR} and PASSWORD=#{passWord,jdbcType=VARCHAR}

mapper.update:
update USER_LOGIN_C
USERNAME=#{userName,jdbcType=VARCHAR}, PASSWORD=#{passWord,jdbcType=VARCHAR}, DEPARTMENT=#{department,jdbcType=VARCHAR}, BIRTHDAY=#{birthday,jdbcType=VARCHAR},
where USER_ID=#{uid,jdbcType=VARCHAR}

update USER_LOGIN_C
USERNAME=#{userName,jdbcType=VARCHAR}, PASSWORD=#{passWord,jdbcType=VARCHAR}, BIRTHDAY=#{birthday,jdbcType=VARCHAR},
where EMAIL=#{email,jdbcType=VARCHAR}
